import "../../css/LoginScreen.css";
import {React,useState,useEffect} from 'react';

function Signin_signup() {

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
  const signUpButton = document.getElementById('signUp');
  const signInButton = document.getElementById('signIn');
  const container = document.getElementById('container');
  const ghostButton = document.querySelectorAll('.ghost');

  const handleSignUpClick = () => {
    container.classList.add("right-panel-active");
    console.log('Sign up button clicked!');
    console.log('Name:', name);
  };

  const handleSignInClick = () => {
    container.classList.remove("right-panel-active");

  };

  const handleGhostClick = (event) => {
    console.log(`Ghost button clicked: ${event.target.id}`);
  };

  signUpButton.addEventListener('click', handleSignUpClick);
  signInButton.addEventListener('click', handleSignInClick);

  ghostButton.forEach((button) => {
    button.addEventListener('click', handleGhostClick);
  });

  // cleanup the event listener
  return () => {
    signUpButton.removeEventListener('click', handleSignUpClick);
    signInButton.removeEventListener('click', handleSignInClick);

    ghostButton.forEach((button) => {
      button.removeEventListener('click', handleGhostClick);
    });
  };
}, [name, email, password]);

  // useEffect(() => {
  //   const signUpButton = document.getElementById('signUp');
  //   const signInButton = document.getElementById('signIn');
  //   const container = document.getElementById('container');

  //   const handleSignUpClick = () => {
  //     container.classList.add("right-panel-active");
  //   };

  //   const handleSignInClick = () => {
  //     container.classList.remove("right-panel-active");
  //   };

  //   signUpButton.addEventListener('click', handleSignUpClick);
  //   signInButton.addEventListener('click', handleSignInClick);

  // }, []);

  return (
    <div className="container" id="container">

      <div className="form-container sign-up-container">

          <form action="#">

              <h1>Create Account</h1>

              <div className="social-container">
                  <a href="#" className="social"><i className="fab fa-facebook-f"></i></a>
                  <a href="#" className="social"><i className="fab fa-google-plus-g"></i></a>
                  <a href="#" className="social"><i className="fab fa-linkedin-in"></i></a>
              </div>

              <span>or use your email for registration</span>

              <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
              <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
              <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />

              <button>Sign Up</button>

          </form>

      </div>

      <div className="form-container sign-in-container">

          <form action="#">

              <h1>Sign in</h1>

              <div className="social-container">

                  <a href="#" className="social"><i className="fab fa-facebook-f"></i></a>
                  <a href="#" className="social"><i className="fab fa-google-plus-g"></i></a>
                  <a href="#" className="social"><i className="fab fa-linkedin-in"></i></a>

              </div>

              <span>or use your account</span>

              <input type="email" placeholder="Email" value={email} onChange={(e)=>setEmail(e.target.value)} />
              <input type="password" placeholder="Password"  value={password} onChange={(e)=>setPassword(e.target.value)}/>

              <a href="#">Forgot your password?</a>
              <button>Sign In</button>

          </form>

      </div>

      <div className="overlay-container">

          <div className="overlay">

              <div className="overlay-panel overlay-left">

                  <h1>Welcome Back!</h1>
                  <p>Ready to shop with us again? Log in and let's get started.</p>
                  <button className="ghost" id="signIn">Sign In</button>

              </div>

              <div className="overlay-panel overlay-right">
                  <h1>Hello, Friend!</h1>
                  <p>Get started with us and enjoy a seamless shopping experience.</p>
                  <button className="ghost" id="signUp">Sign Up</button>
              </div>

          </div>

      </div>

    </div>
  )
}

export default Signin_signup
